mapper.select:
SELECT COUNT(*) FROM ENTERPRISE_TB WHERE 1=1
AND ENT_IDX = #{entIndexKeyword}
AND ENT_NM LIKE '%${entNameKeyword}%'

SELECT * FROM (		
	SELECT @ROWNUM:=@ROWNUM+1 AS RNUM,ENT_IDX,ENT_NM,ENT_BIZR_NO,ENT_INDUTY_CD,ENT_INDUTY_NM,ENT_STLE_TYPE,ENT_SBSCRB_ST,ENT_FL,ENT_FOND_YMD 
	  FROM ENTERPRISE_TB, 				
		(SELECT @ROWNUM := 0) ROWNUM 
			WHERE 1=1 

		  	  AND ENT_IDX = #{entIndexKeyword} 


		  	  AND ENT_NM LIKE '%${entNameKeyword}%'

			) n
WHERE RNUM BETWEEN #{firstRow} AND #{endRow}

SELECT count( distinct ENT_IDX)
FROM VIEW_RECORD_TB
WHERE MBER_IDX=#{MBER_IDX}
