mapper.update:
UPDATE MEMBER_TB 
SET    MBER_ID=#{mberId}, MBER_PW=#{mberPw}, MBER_TYPE=#{mberType}, ENT_IDX=#{entIndex}, USE_FL=#{mberFlag}, OAUTH_ID = #{oauthId}
WHERE  MBER_IDX=#{mberIndex}

UPDATE MEMBER_TB SET
OAUTH_ID = #{oauthId}, LAST_DT=sysdate()
WHERE MBER_ID=#{mberId}

UPDATE MEMBER_TB SET
MBER_TYPE = #{mberType}
WHERE MBER_IDX=#{mberIndex}

UPDATE MEMBER_TB SET
LAST_DT=sysdate()
WHERE MBER_IDX=#{mberIndex}

UPDATE MEMBER_TB SET
MBER_PW = #{mberPw}
WHERE MBER_IDX=#{mberIndex} or MBER_ID=#{mberId}

mapper.select:
SELECT SQL_CALC_FOUND_ROWS MBER_IDX, MBER_ID, MBER_PW, MBER_TYPE, USE_FL, REG_DT, LAST_DT
FROM MEMBER_TB 
WHERE MBER_ID LIKE CONCAT('%',#{keyword},'%')
LIMIT #{NUM_OF_MSG_PER_PAGE}
OFFSET #{START_ROW}

SELECT COUNT(*)
FROM MEMBER_TB
WHERE MBER_ID like CONCAT('%',#{keyword},'%');
