mapper.update:
UPDATE REVIEW_TB
SET `CONTENTS`=#{contents}, EVALUATION=#{evaluationScore}
WHERE ENT_IDX=#{entIndex} AND MBER_IDX=${mberIndex} AND QESTN_NO=#{questionNum}

mapper.select:
 	SELECT SQL_CALC_FOUND_ROWS * 
	FROM REVIEW_QESTN_TB q
	LEFT JOIN REVIEW_TB r
	ON r.QESTN_NO=q.QESTN_NO
	WHERE r.ENT_IDX=#{ENT_IDX} AND r.QESTN_NO=#{QESTN_NO}
	ORDER BY r.REG_DT desc
	LIMIT #{NUM_OF_RVW_PER_PAGE}
	OFFSET #{START_ROW}

SELECT 	 count(*) AS TOTAL_ROWS 
FROM	 REVIEW_TB
WHERE	 ENT_IDX=#{ENT_IDX} AND QESTN_NO=#{QESTN_NO}

SELECT COUNT(*) FROM REVIEW_TB r INNER JOIN MEMBER_TB m ON r.MBER_IDX = m.MBER_IDX INNER JOIN ENTERPRISE_TB e ON r.ENT_IDX = e.ENT_IDX WHERE 1=1
AND r.ENT_IDX = #{entIndexKeyword}
AND e.ENT_NM LIKE '%${entNameKeyword}%'
AND r.QESTN_NO = #{questionNumKeyword}

SELECT * FROM ( SELECT @ROWNUM:=@ROWNUM+1 AS RNUM, r.REVW_IDX, m.MBER_ID, e.ENT_NM, r.ENT_IDX, r.QESTN_NO, r.EVALUATION, r.REVW_FL, r.CONTENTS, r.REG_DT FROM REVIEW_TB r INNER JOIN MEMBER_TB m ON r.MBER_IDX = m.MBER_IDX INNER JOIN ENTERPRISE_TB e ON r.ENT_IDX = e.ENT_IDX, (SELECT @ROWNUM := 0) ROWNUM WHERE 1=1
AND r.ENT_IDX = #{entIndexKeyword}
AND e.ENT_NM LIKE '%${entNameKeyword}%'
AND r.QESTN_NO = #{questionNumKeyword}
) n WHERE RNUM BETWEEN #{firstRow} AND #{endRow}
